    s.component_status_name, p.pro_name, c.single_name, c.floor, c.card_num
    from component AS c
    LEFT JOIN component_status AS s on c.component_status_id=s.component_status_id 
    LEFT JOIN project AS p ON c.pro_id=p.pro_id
    WHERE 1=1

    AND c.pro_id=#{pro_id}


