mapper.select:
SELECT
u.* FROM sys_user u
WHERE u.user_id = #{user_id}

		SELECT * FROM `sys_user`


				valid_status=#{status}


				AND pro_id=#{userProjectID}


				AND user_name like "%"#{keyword}"%"


                AND create_time >= #{startTime}


                AND create_time <= #{endTime}


		order by `create_time` desc

SELECT
u.* FROM sys_user u
WHERE u.user_role_id = #{userRoleID}
