mapper.select:
SELECT * FROM `record`
car_num like "%"#{keyword}"%"
AND leave_time >= #{startTime}
AND leave_time <= #{endTime}
AND car_num is not NULL AND car_num != '' AND entry_time is not NULL AND entry_time != '' AND leave_time is not NULL AND leave_time != ''
order by `leave_time` desc

SELECT * FROM `record`
car_num like "%"#{keyword}"%"
AND leave_time >= #{startTime}
AND leave_time <= #{endTime}
AND (car_num is NULL or car_num = '') AND entry_time is not NULL AND entry_time != '' AND (leave_time is NULL or leave_time = '')
order by `leave_time` desc

